(()=>{(new class{constructor(){const t=document.querySelectorAll(".Carousel__item");this.container=document.querySelector(".Carousel__container"),this.carouselItems=document.querySelector(".Carousel__items"),this.items=t,this.totalItems=t.length,this.nav=document.querySelector(".Carousel__nav"),this.slideItems=4}init(){this.addNav(),this.nav.addEventListener("click",t=>{t.target&&t.target.matches("li")&&this.moveTo(t.target),t.target&&t.target.matches("span")&&this.moveTo(t.target.parentElement)})}addNav(){const t=Math.ceil(this.totalItems/this.slideItems);for(let e=0;e<t;e++){let t=document.createElement("li"),a=document.createElement("span");t.setAttribute("data-slide-to",e),t.className=0===e?"Carousel__navItem active":"Carousel__navItem",t.appendChild(a),this.nav.appendChild(t)}}moveTo(t){document.querySelector(".Carousel__navItem.active").classList.remove("active"),t.classList.add("active");let e=parseInt(t.getAttribute("data-slide-to"))*this.container.offsetWidth;this.carouselItems.style.transform=`translateX(-${e}px)`}}).init()})();