(()=>{(new class{constructor(){const t=document.querySelectorAll(".Carousel__item");this.container=document.querySelector(".Carousel__container"),this.carouselItems=document.querySelector(".Carousel__items"),this.imgWidth=document.querySelector(".Carousel__items img").offsetWidth,this.items=t,this.totalItems=t.length,this.nav=document.querySelector(".Carousel__nav"),this.slideItems=4}init(){this.addNav(),this.carouselItems.setAttribute("data-img-width",this.imgWidth),this.container.addEventListener("click",t=>{t.target&&t.target.matches("img")&&this.onClick(t.target.closest("div"))}),this.nav.addEventListener("click",t=>{t.target&&t.target.matches("li")&&this.moveTo(t.target),t.target&&t.target.matches("span")&&this.moveTo(t.target.parentElement)})}addNav(){const t=Math.ceil(this.totalItems/this.slideItems);for(let e=0;e<t;e++){let t=document.createElement("li"),s=document.createElement("span");t.setAttribute("data-slide-to",e),t.className=0===e?"Carousel__navItem active":"Carousel__navItem",t.appendChild(s),this.nav.appendChild(t)}}moveTo(t){document.querySelector(".Carousel__navItem.active").classList.remove("active"),t.classList.add("active");let e=parseInt(t.getAttribute("data-slide-to"))*this.container.offsetWidth;this.carouselItems.style.transform=`translateX(-${e}px)`}onClick(t){let e,s=document.querySelector(".Carousel__item.active"),a=t.getAttribute("data-slide");s&&(s.classList.remove("active"),e=s.getAttribute("data-slide")),e!==a?(t.classList.add("active"),this.carouselItems.classList.add("active-item")):(t.classList.remove("active"),this.carouselItems.classList.remove("active-item"))}}).init()})();