(()=>{(new class{constructor(){this.hero=document.querySelector(".js-scrollAfter"),this.header=document.querySelector(".SiteHeader")}init(){window.onbeforeunload=(()=>{window.scrollTo(0,0)}),this.throttled(200,window.addEventListener("scroll",this.stickyElement.bind(this)))}stickyElement(){let e=this.hero.offsetHeight,t=this.header.offsetHeight,s=window.scrollY;s>t&&s<e?(this.header.classList.add("is-hidden","theme--White"),this.header.classList.remove("is-fixed","theme--Transparent")):s>e?(this.header.classList.add("is-fixed"),this.header.classList.remove("is-hidden")):(this.header.classList.add("theme--Transparent"),this.header.classList.remove("is-hidden","is-fixed","theme--White"))}throttled(e,t){let s=0;return(...i)=>{const r=(new Date).getTime();if(!(r-s<e))return s=r,t(...i)}}}).init()})();