(()=>{(new class{constructor(){this.nav=document.querySelector(".Tabs__nav")}init(){this.nav.addEventListener("click",e=>{e.target&&e.target.matches("li")&&this.onTabClick(e.target)})}onTabClick(e){let t=document.querySelector(".Tabs__navItem.active"),a=document.querySelector(".Tabs__pane.active");t.classList.remove("active"),a.classList.remove("active"),e.className+=" active",document.getElementById(e.getAttribute("data-tab")).className+=" active"}}).init()})();